<?phpnamespace common\models\common\user;use Yii;/** * This is the model class for table "{{%user_extend}}". * * @property integer $id * @property integer $uid * @property integer $point * @property string $money * @property string $freez_money * @property integer $level */class UserExtend extends \yii\db\ActiveRecord{    /**     * @inheritdoc     */    public static function tableName()    {        return '{{%user_extend}}';    }    /**     * @inheritdoc     */    public function rules()    {        return [            [['uid'], 'required'],            [['uid', 'point', 'level'], 'integer'],            [['money', 'freez_money'], 'number'],        ];    }    /**     * @inheritdoc     */    public function attributeLabels()    {        return [            'id' => 'ID',            'uid' => '用户ID',            'point' => '用户积分',            'money' => '用户余额',            'freez_money' => '冻结金额',            'level' => '等级',        ];    }}