<?phpnamespace frontend\controllers;use Yii;use yii\web\Controller;use frontend\components\common\user\WxUserBase;use frontend\components\common\cash\CashBase;/** * Summary of IndexController * 网站路由默认入口控制器 */class QiyoMyController extends Controller{    public function beforeAction($action)    {        return true;    }    //页面初始化-加载主微信配置    public  function init()    {        }        public function actionIndex()    {               $CUser=new WxUserBase();        $uInfo=$CUser->getUInfo(); //用户信息             $mInfo=  CashBase::getUserMoney($uInfo['id']);//当前余额信息        return  $this->render("my",[            'arrUserInfo'=>$uInfo,            'arrCashInfo'=>$mInfo       ]);    }    }