<?phpnamespace frontend\modules\common\controllers;use Yii;use yii\web\Controller;use frontend\modules\common\components\CVer;/** * Summary of IndexController  网站路由默认入口控制器 */class ApiController extends Controller{         public $layout=false;    public function beforeAction($action)    {        return true;    }            public function actionSendcode()    {        $reData=[            'status'=>404,            'message'=>'未设置',            ];                $tel=Yii::$app->request->post("tel");        CVer::send($tel,'huaju');                $reData=[           'status'=>0,           'message'=>'验证码已发送',           ];                 echo json_encode($reData);        exit;    }      }